mapper.insert:
insert into users (id, nickname, email, 
  password, name, ic, 
  phone, status, active, 
  createTime, updateTime, deviceToken
  )
values (#{id,jdbcType=INTEGER}, #{nickname,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, 
  #{password,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{ic,jdbcType=VARCHAR}, 
  #{phone,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{active,jdbcType=INTEGER}, 
  #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}, #{devicetoken,jdbcType=VARCHAR}
  )

mapper.select:
    select 

	from User
    where phone = #{phone,jdbcType=VARCHAR}
    and password = #{password,jdbcType=VARCHAR}
    and status = 1
    and active = 1
